            .sidebar {
                display: flex;
                flex-direction: column;
                position: fixed;
                top: 0;
                left: 0;
                height: 100%;
                width: 69px;
                background-color: #6785fe;
                padding: 30px 24px;
                box-shadow: 2px 0 5px rgba(0, 0, 0, 0.1);
                
            }

            .sidebar svg {
                width: 22px;
                height: 22px;
                margin-bottom: 49px;
            }

            .side-info {
                visibility: hidden;
                opacity: 0;
                position: absolute;
                left: 100%;
                background: #333;
                color: #fff;
                padding: 11px 14px;
                font-size: 13px;
                border-radius: 4px;
                top: 15%;
                white-space: nowrap;
                transform: translateX(-5px) scale(0.7);
                transform-origin: left;
                font-size: 15px;
                transition: all 500ms cubic-bezier(0.19, 1, 0.22, 1);
                transition-timing-function: cubic-bezier(0.19, 1, 0.22, 1);
            }

            .side-info.visible {
                visibility: visible;
                opacity: 1;
                transform: translateY(0px) scale(1);
            }

            .material-symbols-outlined {
                margin-top: 0.5%;
                font-variation-settings: 'FILL' 0, 'wght' 400, 'GRAD' 0, 'opsz' 24
            }

            .myphoto img {
                width: 30px;
                border-radius: 100px;
            }
            .menu a.active {
                color: #0466c8; /* Цвет для активной ссылки */
            }

